function pad(s) {
  return s < 10 ? `0${s}` : s
}

function formatDate(timeStamp){
	let diff = (Date.now() - timeStamp * 1000) / 1000
    if (diff < 60) {
      return 'åˆšåˆš'
    } else if (diff < 3600) {
      return `${parseInt(diff / 60)}åˆ†é’Ÿå‰`
    } else if (diff < 86400) {
      return `${parseInt(diff / 3600)}å°æ—¶å‰`
    } else if (diff < 604800) {
      return `${parseInt(diff / 86400)}å¤©å‰`
    } else if (diff < 2592000) {
      return `${parseInt(diff / 604800)}å‘¨å‰`
    } else if (diff < 31104000) {
      return `${parseInt(diff / 2592000)}ä¸ªæœˆå‰`
    } else {
      let date = new Date(timeStamp * 1000)
      return `${date.getFullYear()}-${pad(date.getMonth() + 1)}-${pad(date.getDate())}`
    }
	}

function createReply(item, addPadding) {
	  let html = "";
    addPadding?fu = "â–²":fu=""
    html += `<br>&lrm;<br>---${fu}${item.username}â–ª${formatDate(item.dateline)}---<br>`
    html += `${item.message}`
